var webtap = require('../..');

var test = webtap(function (req, res) {
  res.statusCode = 404;
  res.end('file not found');
});

test('GET', '/', function (t, res) {
  t.equal(res.statusCode, 404, 'status code 404');
  t.body('file not found', 'friendly message');
});

test('custom name', 'POST', '/', function (t, res) {
  t.equal(res.statusCode, 404, 'status code 404');
  t.body('file not found', 'friendly message');
});

test('GET /', function (t, res) {
  t.body('file not found', 'friendly message');
})

test('shorthand', 'GET /', function (t, res) {
  t.body('file not found', 'friendly message');
})

